--
-- Name: articles_tsvector_trigger(); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.articles_tsvector_trigger() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
BEGIN
  IF TG_OP = 'INSERT'
     OR NEW.title     IS DISTINCT FROM OLD.title
     OR NEW.summary   IS DISTINCT FROM OLD.summary
     OR NEW.full_text IS DISTINCT FROM OLD.full_text
  THEN
    NEW.search_vector :=
         setweight(to_tsvector('simple', coalesce(NEW.title,'')),     'A')
      || setweight(to_tsvector('simple', coalesce(NEW.summary,'')),   'B')
      || setweight(to_tsvector('simple', coalesce(NEW.full_text,'')), 'C');
  END IF;
  RETURN NEW;
END
$$;


--
-- Name: purge_old_articles(integer); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.purge_old_articles(p_days integer DEFAULT 7) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
  n_deleted integer;
BEGIN
  DELETE FROM articles
  WHERE published_at IS NOT NULL
    AND published_at < now() - make_interval(days => p_days);

  GET DIAGNOSTICS n_deleted = ROW_COUNT;  -- how many rows were deleted
  RETURN n_deleted;
END
$$;


